        this.listDeJogos = [];
    }
    async todosJogos() {
        return await fetch(this.urlBack, { method: 'GET' })
            .then((res) => res.json())
            .then((res) => res.content);
        // return this.httpClient.get(`${this.urlBack}`)
    }
    get jogos() {
        return this.listDeJogos;
    }
    adicionar(game) {
        this.hidratar(game);
        this.listDeJogos.push(game);
        return this.httpClient.post(this.urlBack, game);
    }
    hidratar(game) {
        game.lancamento = new Date().toString();
    }
}
